public ItemStack formatMeta(ItemStack itemstack, String displayName, List<String> lore, List<Enchantment> enchants) {
ItemMeta meta = itemstack.getItemMeta();
meta.setDisplayName(displayName);
for(Enchantment enchant : enchants) {
meta.addEnchant(enchant.getEnchantEnum(), enchant.getLevel(), true);
}
meta.setLore(lore);
itemstack.setItemMeta(meta);
return itemstack;
}
